mapper.insert:
insert into mpms_category (parent_id, code, level, 
  name, full_name, srt, 
  is_viewable, is_disabled, is_deleted, cruser, 
  crtime
  )
values (#{parentId,jdbcType=BIGINT}, #{code,jdbcType=VARCHAR}, #{level,jdbcType=INTEGER}, 
  #{name,jdbcType=VARCHAR}, #{fullName,jdbcType=VARCHAR}, #{srt,jdbcType=INTEGER}, 
  #{viewable,jdbcType=BIT}, 0, 0, #{cruser,jdbcType=VARCHAR}, 
  #{crtime,jdbcType=TIMESTAMP}
  )

mapper.select:
select 

from mpms_category
where is_deleted = 0 AND id = #{id,jdbcType=BIGINT}

sql.where:
is_deleted = 0

  and is_disabled = #{disabled,jdbcType=BIT}


  and is_viewable = #{viewable,jdbcType=BIT}


  and parent_id = #{parentId,jdbcType=BIGINT}


  
  and name LIKE #{name,jdbcType=VARCHAR}


  
  and full_name LIKE #{fullName,jdbcType=VARCHAR}

mapper.update:
update mpms_category
set
is_deleted = 1,
mduser = #{mduser,jdbcType=VARCHAR},
mdtime = #{mdtime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select

from mpms_category

ORDER BY ${sort} ${order}
limit ${offset},${rows}
