mapper.select:
select

from sys_dictionary
where is_deleted = 0 and id = #{id,jdbcType=BIGINT}

sql.where:
is_deleted = 0


    and `name` LIKE #{name,jdbcType=VARCHAR}


    and parent_code IS NULL


    and parent_code = #{parentCode,jdbcType=VARCHAR}

mapper.update:
update sys_dictionary`
set
is_deleted = 1,
mduser = #{mduser,jdbcType=VARCHAR},
mdtime = #{mdtime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select

from sys_dictionary

ORDER BY ${sort} ${order}
limit ${offset},${rows}

select

from sys_dictionary
where is_deleted = 0 and code = #{code,jdbcType=VARCHAR}

select

from sys_dictionary
where is_deleted = 0 and parent_code = #{parentCode,jdbcType=VARCHAR}
